INSERT INTO DATABASESTRUCTUREDESCRIPTIONS (DESCRIPTION,FIELDNAME,HASREALZEROVALUES,NOTINUSE,NOTINUSENOTES,REPORTGROUP,REPORTLABEL,TABLENAME,VALUETRANSLATIONS) VALUES ('Date last modified','MODIFIED',0,1,'Starting in version 4.3 of WebCAMP, PATENT and its child tables (PERSONPATENT, PROTOCOLPATENT, SACAPP_PROTOCOLPATENT) are no longer used to track patents.  Instead, patents are handled as children of inventions; thus, the primary patent table is INVENTIONPATENT, which allows for any number of patents linked to a single invention.  PERSONPATENT, PROTOCOLPATENT and SACAPP_PROTOCOLPATENT are replaced by INVENTIONPERSON, INVENTIONPROTOCOL and INVENTIONSACAPP_PROTOCOL (i.e., the linkage to protocols and inventors is via the INVENTION record).  The deprecated tables are not being removed from the database, as they were once used for PROMPTR surveys and are therefore maintained for backward compatibility (i.e., the ability to view those surveys as they appeared at the time they were conducted).','','','PATENT','');
